for(var Tooltips=document.getElementsByClassName("TooltipTrigger"),i=0;i<Tooltips.length;i++)Tooltips[i].addEventListener("mouseenter",function(t){t.preventDefault(),this.style.position="relative",this.innerHTML=this.innerHTML+"<div class='Tooltips'><p class='"+this.getAttribute("data-position")+"'>"+this.getAttribute("data-tooltip")+"</p></div>"}),Tooltips[i].addEventListener("mouseleave",function(t){t.preventDefault(),this.removeAttribute("style"),this.innerHTML=this.innerHTML.replace(/<div[^]*?<\/div>/,"")});